from godot import exposed, export, Node2D, preload_scene, load_resource, AnimationPlayer, Animation

@exposed
class AnimationPlayer2D(Node2D):
    animations_path = export(str)
    animation_name = export(str)

    def _ready(self):
        self.animation_player = self.get_node("AnimationPlayer")
        self.load_animations()
        if self.animation_name:
            self.play_animation(self.animation_name)

    def load_animations(self):
        animations_file = preload_scene(self.animations_path)
        animations = animations_file.get_node("Animations")
        for child in animations.get_children():
            if isinstance(child, Animation):
                self.animation_player.add_animation(child.name, child)

    def play_animation(self, animation_name):
        if self.animation_player.has_animation(animation_name):
            self.animation_player.play(animation_name)
```

